rule "WINDOWS SYSMON EVENT 3 LOG NORMALIZATION"
when
    $message.rule_group1 == "windows" AND $message.rule_group3 == "sysmon_event3"
then
    set_field("process_image", $message.data_win_eventdata_image);
    set_field("process_id", $message.data_win_eventdata_processId);
    set_field("protocol", $message.data_win_eventdata_protocol);
    set_field("user_name", $message.data_win_eventdata_user);
    set_field("dst_ip", $message.data_win_eventdata_destinationIp);
    set_field("dst_port", $message.data_win_eventdata_destinationPort);
    set_field("src_ip", $message.data_win_eventdata_sourceIp);
    set_field("src_port", $message.data_win_eventdata_sourcePort);
end

rule "AbuseIPDB Lookup on DestIP"
when
    has_field("DestIP")
then
    // Perform lookup using the AbuseIPDB Lookup Table
    let ldata = lookup(
        lookup_table: "abuseipdb",
        key: to_string($message.DestIP)
    );
    set_fields(
        fields: ldata,
        prefix: "abuseipdb_"
    );
end

rule "LINUX SYSMON EVENT 3 LOG NORMALIZATION"
when
    $message.rule_group1 == "linux" AND $message.rule_group3 == "sysmon_event3"
then
    set_field("process_image", $message.data_eventdata_image);
    set_field("process_id", $message.data_eventdata_processId);
    set_field("protocol", $message.data_eventdata_protocol);
    set_field("user_name", $message.data_eventdata_user);
    set_field("dst_ip", $message.data_eventdata_DestinationIp);
    set_field("dst_port", $message.data_eventdata_destinationPort);
    set_field("src_ip", $message.data_eventdata_sourceIp);
    set_field("src_port", $message.data_eventdata_sourcePort);
end
